import React, { useEffect, useState } from "react";
import axios from "axios";
import './styles/ReportDashboard.css'


function ReportDashboard() {

        const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [username, setUsername] = useState("");
    const [schoolname, setSchoolname] = useState("");
    const [licence, setLicence] = useState("");

    const [newStudent, setNewStudent] = useState("");

    useEffect(() => {
        axios.get('http://localhost:3001/reportdashboard').then((response) => {
            setStudentList(response.data)
        });

        console.log('Report been created');

    }, [setStudentList])


    const deleteHandler = (name) => {
        axios.delete(`http://localhost:3001/api/delete/${name}`);
        console.log(`Report was deleted`);
    }


    const updateHandler = (name) => {
        axios.put("http://localhost:3001/api/update", {
            firstname: name,
            lastname: newStudent,
        });
        setNewStudent("")
    };

    return <div>
        {studentList.map(student => {
            return (
                <div className="container" key={student._id}>
                    <table>
                        <tbody>
                            <tr>
                                <td>First name: {student.firstname} </td>
                            </tr>
                            <tr>
                                <td>Last name: {student.lastname} </td>
                            </tr>
                            <tr>
                                <td>Username: {student.username} </td>
                            </tr>
                            <tr>
                                <td>School name: {student.schoolname} </td>
                            </tr>
                            <tr>
                                <td>Licence: {student.licence} </td>
                            </tr>
                        </tbody>
                    </table>


                    <div className="repsInput">

                        <input type="text" onChange={(event) => { setNewStudent(event.target.value) }} placeholder="first name"></input>

                        <input type="text" onChange={(event) => { setNewStudent(event.target.value) }} className="form" placeholder="last name"></input>

                        <input type="text" onChange={(event) => { setNewStudent(event.target.value) }} className="form" placeholder="username"></input>

                        <input type="text" onChange={(event) => { setNewStudent(event.target.value) }} className="form" placeholder="school name"></input>

                        <input type="text" onChange={(event) => { setNewStudent(event.target.value) }} className="form" placeholder="licence"></input>

                        <button style={{
                            position: "absolute", right: "30px", top: "40px",
                            width: "10%",
                            background: "black",
                            color: "white"

                        }}
                            onClick={() => deleteHandler(student.firstname)}>DELETE</button>

                        <button style={{
                            position: "absolute", right: "30px", top: "90px", width: "10%", background: "black",
                            color: "white"
                        }}
                            onClick={() => updateHandler(student.firstname)}>UPDATE</button>

                    </div>
                </div>
            )
        })}

    </div>
}

export default ReportDashboard;
