import React, { useEffect, useState } from "react";
import axios from "axios";
import './styles/ReportDashboard.css'


function ReportDashboard() {
    const [reps, setReps] = useState([{
        _id: '',
        state: '',
        reportType: '',
        message: ''
    }])

    const [isPut, setIsPut] = useState(false);

    const [updatedRep, setUpdatedRep] = useState({
        _id: '',
        state: '',
        reportType: '',
        message: ''
    })

    useEffect(() => {
        fetch("/reportdashboard")
            .then(res => {
                return res.json()

            }).then(jsonRes =>
                setReps(jsonRes));

    })

    function deleteHandler(_id) {
        axios.delete('/delete/' + _id);
        alert("report is blocked and deleted")
    }


    function openUpdate(_id) {
        setIsPut(true);
        setUpdatedRep(prevInput => {
            return (
                {
                    ...prevInput,
                    id: _id,
                }
            )
        })
    }

    // function updateHandler(_id) {
    //     axios.put('/reports/' + _id);
    //     alert('Report updated');
    //     console.log(`Report with if ${_id} updated`);
    // }

    return <div>
        {reps.map(rep => {
            return (
                <div className="container" key={rep._id}>
                    <h2>Report</h2>
                    <table>
                        <tbody>
                            <tr>
                                <td>ID: {rep._id} </td>
                            </tr>
                            <tr>
                                <td>State: {rep.state} </td>
                            </tr>
                            <tr>
                                <td>Type: {rep.reportType} </td>
                            </tr>
                            <tr>
                                <td>Message: {rep.message} </td>
                            </tr>
                        </tbody>
                    </table>

                    <button onClick={() => deleteHandler(rep._id)}>BLOCK</button>
                    <button onClick={() => openUpdate(rep._id)}>RESOLVE</button>

                    <hr />
                    {reps.map(rep => {
                        return (
                            <div>
                                <input name="_id" value={rep._id} placeholder="id"></input>
                                <input name="state" value={rep.state} placeholder="state"></input>
                                <input name="reportType" value={rep.reportType} placeholder="type"></input>
                                <input name="message" value={rep.message} placeholder="message"></input>

                                <button>Update</button>
                            </div>
                        )
                    })}

                </div>

            )
        })}
    </div>
}

export default ReportDashboard;
