import React, { useState } from "react";
import axios from "axios";

function Reports() {

    const [input, setInput] = useState([{
        state: '',
        type: '',
        message: ''
    }]);

    // useEffect(() => {
    //     fetch("/reports")
    //         .then(res => {
    //             if (res.ok) {
    //                 console.log(res)
    //                 return res.json()
    //             }
    //         }).then(jsonRes =>
    //             setInput(jsonRes));
    // }, [])


    function handleChange(event) {
        const { name, value } = event.target;

        setInput(prevInput => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    }

    function handleClick(event) {
        event.preventDefault();
        const newRep = {
            state: input.state,
            type: input.type,
            message: input.message
        }
        // console.log(newRep)
        // axios.post('http://localhost3001/reports', newRep)
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({newRep: 'Here is post req'})
        };
        fetch('http://localhost3001/reports', newRep)
        .then(res => res.json)
    }

    return <div className="container">
        <h1>Report Page</h1>
        <h2>Create report</h2>
        <form>
            <div className="form-group">
                <input onChange={handleChange} name="state" value={input.state} className="form-control" placeholder="state"></input>
            </div>

            <div className="form-group">
                <input onChange={handleChange} name="type" value={input.type} className="form-control" placeholder="type"></input>
            </div>

            <div className="form-group">
                <textarea onChange={handleChange} name="message" value={input.message} className="form-control" placeholder="message"></textarea>
            </div>

            <button onClick={handleClick} className="btn btn-lg btn-info">Add report</button>
        </form>
    </div>
}

export default Reports;
