import React, { useEffect, useState } from "react";
import axios from "axios";
// import SignleReport from './SingleReport';


function ReportDashboard() {
    const [reps, setReps] = useState([{
        reportId: '',
        state: '',
        reportType: '',
        message: ''
    }])

    useEffect(() => {
        fetch("/reportdashboard")
            .then(res => {
                return res.json()

            }).then(jsonRes =>
                setReps(jsonRes));

    })

    function deleteHandler(reportId) {
        axios.delete('/delete/' + reportId);
        alert("report is blocked and deleted")
    }

    return <div className="container">
        <h2>Report Dashboard</h2>

        {reps.map(rep => {
            return (
                // <div key={rep.id}>
                //     <SignleReport state={rep.state} reportType={rep.reportType} message={rep.message} reps={reps} setReps={setReps} />
                // </div>
                <div key={rep.reportId}>
                    <tr>
                        <td>ID: {rep.id}</td>
                    </tr>
                    <tr>
                        <td>State: {rep.state}</td>
                    </tr>
                    <tr>
                        <td>Type: {rep.reportType}</td>
                    </tr>
                    <tr>
                        <td>Message: {rep.message}</td>
                    </tr>

                    <button onClick={() => deleteHandler(rep.id)}>BLOCK</button>

                    <button>RESOLVE</button>
                </div>
            )
        })}
    </div>
}

export default ReportDashboard;
