import React, { useState } from "react";
import axios from "axios";
import './styles/CreateReport.css'

function CreateReport() {

    const [input, setInput] = useState({
        firstname: '',
        lastname: '',
        username: '',
        schoolname: '',
        licence: ''
    });

    function handleChange(event) {
        const { name, value } = event.target;
        setInput(prevInput => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    }

    function handleClick(event) {
        event.preventDefault();
        const newRep = {
            firstname: input.firstname,
            lastname: input.lastname,
            reportType: input.reportType,
            message: input.message
        }

        axios.post('http://localhost:3001/reports', newRep);
        alert('Report been created')
        console.log('Report been created');
    }

    return <div className="container">
        <h2>Add Student</h2>

        <form>
            <div className="form-group">
                <input onChange={handleChange} name="_id" value={input.firstname} className="form-control" placeholder="id"></input>
            </div>
            
            <div className="form-group">
                <input onChange={handleChange} name="state" value={input.lastname} className="form-control" placeholder="state"></input>
            </div>

            <div className="form-group">
                <input onChange={handleChange} name="reportType" value={input.reportType} className="form-control" placeholder="type"></input>
            </div>

            <div className="form-group">
                <textarea onChange={handleChange} name="message" value={input.message} className="form-control" placeholder="message"></textarea>
            </div>

            <button onClick={handleClick} className="btn btn-lg btn-info">Add student</button>
        </form>

    </div>
}

export default CreateReport;
