import React, { useEffect, useState } from "react";
import axios from "axios";
import './styles/ReportDashboard.css'


function ReportDashboard() {

    const [studentList, setStudentList] = useState([]);

    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [username, setUsername] = useState("");
    const [schoolname, setSchoolname] = useState("");
    const [licence, setLicence] = useState("");


    useEffect(() => {
        axios.get('http://localhost:3001/reportdashboard').then((response) => {
            setStudentList(response.data)
        });

        console.log('Report been created');

    }, [setStudentList])


    const deleteHandler = (name) => {
        axios.delete(`http://localhost:3001/api/delete/${name}`);
        console.log(`Report was deleted`);
    }


    const updateHandler = (name) => {
        axios.put("http://localhost:3001/api/update", {
            firstname: firstname,
            lastname: lastname,
            username: username,
            schoolname: schoolname,
            licence: licence,
        });
        console.log(`Report updated`);
    };

    return <div>
        {studentList.map(student => {
            return (
                <div className="container" key={student._id}>
                    <table>
                        <tbody>
                            <tr>
                                <td>First name: {student.firstname} </td>
                            </tr>
                            <tr>
                                <td>Last name: {student.lastname} </td>
                            </tr>
                            <tr>
                                <td>Username: {student.username} </td>
                            </tr>
                            <tr>
                                <td>School name: {student.schoolname} </td>
                            </tr>
                            <tr>
                                <td>Licence: {student.licence} </td>
                            </tr>
                        </tbody>
                    </table>

                    <button style={{
                        position: "absolute", right: "30px", top: "40px",
                        width: "10%"
                    }}
                        onClick={() => deleteHandler(student.firstname)}>DELETE</button>

                    <button style={{
                        position: "absolute", right: "30px", top: "90px", width: "10%"
                    }}
                        onClick={() => updateHandler(student.firstname)}>UPDATE</button>

                    <hr />

                    <div className="repsInput">

                        <input onChange={(event) => { setFirstname(event.target.value) }} placeholder="firstname"></input>

                        <input type="text"
                            onChange={(event) => {
                                setFirstname(event.target.value);
                            }} className="form-control" placeholder="school name"></input>
