import React, { useState } from "react";
import axios from "axios";
import './styles/ReportDashboard.css'


function ReportDashboard() {

    const [studentList, setStudentList] = useState([]);

    const [updatedRep, setUpdatedRep] = useState({
        firstname: '',
        lastname: '',
        username: '',
        schoolname: '',
        licence: '',
    })


    const getStudentList = () => {
        axios.get('http://localhost:3001/reportdashboard').then((response) => {
            setStudentList(response.data)
        });

        console.log('Report been created');
    }

    const deleteHandler = (id) => {
        axios.delete('/delete/' + id);
        alert("report is blocked and deleted");
        console.log(`Report with id ${id} deleted`);
    }


    const updateHandler = (id) => {
        axios.put('/put/' + id, updatedRep);
        alert('Report updated');
        console.log(`Report with id ${id} updated`);
    };

    const handleUpdate = (event) => {
        const { name, value } = event.target;
        setUpdatedRep(prevInput => {
            return (
                {
                    ...prevInput,
                    [name]: value
                }
            );
        });
    }

    return <div>
        <button onClick={getStudentList}>Show Employees</button>
        {studentList.map(student => {
            return (
                <div className="container" key={student._id}>
                    <h2>Students</h2>
                    <table>
                        <tbody>
                            <tr>
                                <td>First name: {student.firstname} </td>
                            </tr>
                            <tr>
                                <td>Last name: {student.lastname} </td>
                            </tr>
                            <tr>
                                <td>Username: {student.username} </td>
                            </tr>
                            <tr>
                                <td>School name: {student.schoolname} </td>
                            </tr>
                            <tr>
                                <td>Licence: {student.licence} </td>
                            </tr>
                        </tbody>
                    </table>

                    <button style={{
                        position: "absolute", right: "30px", top: "40px",
                        width: "10%"
                    }}
                        onClick={() => deleteHandler(student._id)}>DELETE</button>

                    <button style={{
                        position: "absolute", right: "30px", top: "90px", width: "10%"
                    }}
                        onClick={() => updateHandler(updatedRep._id)}>UPDATE</button>

                    <hr />

                    <div className="repsInput">

                        <input onChange={handleUpdate} name="_id" value={updatedRep.firstname} placeholder="id"></input>

                        <input onChange={handleUpdate} name="state" value={updatedRep.lastname} placeholder="state"></input>

                        <input onChange={handleUpdate} name="reportType" value={updatedRep.username} placeholder="type"></input>

                        <input onChange={handleUpdate} name="message" value={updatedRep.schoolname} placeholder="message"></input>

                        <input onChange={handleUpdate} name="message" value={updatedRep.licence} placeholder="message"></input>

                    </div>
                </div>
            )
        })}

    </div>
}

export default ReportDashboard;
