import React, { useState } from "react";
import axios from "axios";
import './styles/CreateReport.css'

function CreateReport() {

    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [username, setUsername] = useState("");
    const [schoolname, setSchoolname] = useState("");
    const [licence, setWage] = useState(0);

    function handleChange(event) {
        const { name, value } = event.target;
        setInput(prevInput => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    }

    function addStudent(event) {
        event.preventDefault();
        const newInput = {
            firstname: input.firstname,
            lastname: input.lastname,
            username: input.username,
            schoolname: input.schoolname,
            licence: input.licence
        }

        axios.post('http://localhost:3001/reports', newInput).then(() => {
            console.log("success")
        });
       
        console.log('Report been created');
    }

    return <div className="container">
        <h2>Add Student</h2>

        <form>
            <div className="form-group">
                <input onChange={handleChange} name="_id" value={input.firstname} className="form-control" placeholder="firstname"></input>
            </div>
            
            <div className="form-group">
                <input onChange={handleChange} name="state" value={input.lastname} className="form-control" placeholder="lastname"></input>
            </div>

            <div className="form-group">
                <input onChange={handleChange} name="username" value={input.username} className="form-control" placeholder="username"></input>
            </div>

            <div className="form-group">
                <textarea onChange={handleChange} name="schoolname" value={input.schoolname} className="form-control" placeholder="schoolname"></textarea>
            </div>
            <div className="form-group">
                <textarea onChange={handleChange} name="licence" value={input.licence} className="form-control" placeholder="licence"></textarea>
            </div>
            <button onClick={addStudent} className="btn btn-lg btn-info">Add student</button>
        </form>

    </div>
}

export default CreateReport;
