import React, { useEffect, useState } from "react";


function Reports() {

    const [input, setReports] = useState([{
        state: '',
        type: '',
        message: ''
    }]);

    useEffect(() => {
        fetch("/reports")
            .then(res => {
                if (res.ok) {
                    console.log(res)
                    return res.json()
                }
            }).then(jsonRes =>
                setReports(jsonRes));
    }, [])


    function handleChange(event) {
        const { state, value } = event.target;
        setReports(prevInput => {
            return {
                ...prevInput,
                [state]: value
            }
        })
    }

    function handleClick(event) {
        event.preventDefault();
        console.log(reports);
    }

    return <div className="container">
        <h1>Report Page</h1>
        <h2>Create report</h2>
        <form>
            <div className="form-group">
                <input onChange={handleChange} state="state" value={reports.state} autoComplete="off" className="form-control" placeholder="report state"></input>
            </div>

            <div className="form-group">
                <input onChange={handleChange} type="type" value={reports.type} autoComplete="off" className="form-control" placeholder="report type"></input>
            </div>

            <div className="form-group">
                <textarea onChange={handleChange} message="message" value={reports.message}  autoComplete="off" className="form-control" placeholder="report message"></textarea>
            </div>

            <button onClick={handleClick} className="btn btn-lg btn-info">Add report</button>
        </form>
    </div>
}

export default Reports;
